const salesData = $input.all()[0].json.salesData;
const textData = $input.all()[0].json.textData;
const customerFeedback = $input.all()[0].json.customerFeedback;
const cryptoData = $input.all()[1].json.cryptoData;
const imageData = $input.all()[1].json.imageData;
const validationRules = $input.all()[1].json.validationRules;

const results = {};

// 1. Data Analysis with Lodash
results.salesAnalysis = {
  totalRevenue: lodash.sumBy(salesData, item => item.price * item.quantity),
  averageOrderValue: lodash.meanBy(salesData, item => item.price * item.quantity),
  topProduct: lodash.maxBy(salesData, item => item.price * item.quantity),
  salesByCountry: lodash.groupBy(salesData, 'customer.country'),
  uniqueProducts: lodash.uniqBy(salesData, 'product').map(item => item.product)
};

// 2. Date Processing with dayjs
results.dateAnalysis = {
  salesDates: salesData.map(sale => ({
    original: sale.date,
    formatted: dayjs(sale.date).format('MMMM D, YYYY'),
    daysAgo: dayjs().diff(dayjs(sale.date), 'day'),
    quarter: 'Q' + Math.ceil((dayjs(sale.date).month() + 1) / 3),
    weekday: dayjs(sale.date).format('dddd')
  })),
  currentTime: dayjs().format('YYYY-MM-DD HH:mm:ss'),
  timezone: dayjs.tz.guess()
};

// 3. Text Analysis with string-similarity and sentiment
const words = textData.split(' ').filter(word => word.length > 4);
const techTerms = ['machine', 'learning', 'data', 'artificial', 'intelligence', 'cloud', 'computing'];

results.textAnalysis = {
  wordCount: words.length,
  uniqueWords: lodash.uniq(words).length,
  mostFrequent: lodash.chain(words)
    .countBy()
    .toPairs()
    .sortBy(1)
    .reverse()
    .take(5)
    .fromPairs()
    .value(),
  techRelevance: techTerms.map(term => ({
    term,
    similarity: stringSimilarity.findBestMatch(term, words).bestMatch.rating
  })),
  averageRating: lodash.meanBy(customerFeedback, 'rating'),
  sentimentScores: customerFeedback.map(feedback => ({
    rating: feedback.rating,
    comment: feedback.comment.substring(0, 50) + '...',
    positiveWords: feedback.comment.match(/excellent|great|good|best|outstanding|perfect/gi) || []
  }))
};

// 4. Cryptography with CryptoJS
const encrypted = CryptoJS.AES.encrypt(cryptoData.message, cryptoData.secret).toString();
const decrypted = CryptoJS.AES.decrypt(encrypted, cryptoData.secret).toString(CryptoJS.enc.Utf8);

results.cryptography = {
  originalMessage: cryptoData.message,
  encrypted: encrypted,
  decrypted: decrypted,
  md5Hash: CryptoJS.MD5(cryptoData.message).toString(),
  sha256Hash: CryptoJS.SHA256(cryptoData.message).toString(),
  hmac: CryptoJS.HmacSHA256(cryptoData.message, cryptoData.apiKey).toString(),
  base64: CryptoJS.enc.Base64.stringify(CryptoJS.enc.Utf8.parse(cryptoData.message))
};

// 5. Validation with Joi
const schema = Joi.object({
  email: Joi.string().email().required(),
  age: Joi.number().min(18).max(100),
  username: Joi.string().alphanum().min(3).max(20)
});

const testData = [
  { email: 'valid@email.com', age: 25, username: 'user123' },
  { email: 'invalid-email', age: 150, username: 'us' },
  { email: 'test@test.com', age: 17, username: 'valid_user' }
];

results.validation = testData.map(data => {
  const validation = schema.validate(data);
  return {
    input: data,
    isValid: !validation.error,
    errors: validation.error ? validation.error.details.map(d => d.message) : []
  };
});

// 6. UUID Generation
results.identifiers = {
  transactionIds: salesData.map(sale => ({
    product: sale.product,
    uuid: uuid.v4(),
    shortId: nanoid.nanoid(10)
  })),
  sessionId: uuid.v4(),
  apiKey: nanoid.nanoid(32)
};

// 7. Data Formatting
results.formatting = {
  slugs: lodash.uniq(salesData.map(s => s.product)).map(product => ({
    original: product,
    slug: slug(product),
    kebab: lodash.kebabCase(product),
    snake: lodash.snakeCase(product)
  })),
  plural: {
    '1 item': pluralize('item', 1),
    '5 items': pluralize('item', 5),
    '1 person': pluralize('person', 1),
    '3 people': pluralize('person', 3)
  },
  bytes: {
    '1024': bytes(1024),
    '1048576': bytes(1048576),
    '1073741824': bytes(1073741824)
  }
};

// 8. Advanced Data Processing
const csvData = papaparse.unparse(salesData.map(sale => ({
  Date: sale.date,
  Product: sale.product,
  Price: sale.price,
  Quantity: sale.quantity,
  Total: sale.price * sale.quantity,
  Customer: sale.customer.name,
  Country: sale.customer.country
})));

results.dataExport = {
  csvPreview: csvData.split('\n').slice(0, 5).join('\n') + '\n...',
  jsonSize: bytes(JSON.stringify(salesData).length),
  yamlPreview: YAML.stringify(salesData[0])
};

// 9. Math Operations
const revenues = salesData.map(s => s.price * s.quantity);
results.statistics = {
  mean: math.mean(revenues),
  median: math.median(revenues),
  std: math.std(revenues),
  variance: math.variance(revenues),
  sum: math.sum(revenues),
  expression: math.evaluate('sqrt(3^2 + 4^2)')
};

// 10. Summary Dashboard
results.dashboard = {
  totalOrders: salesData.length,
  totalRevenue: results.salesAnalysis.totalRevenue,
  topProduct: results.salesAnalysis.topProduct.product,
  averageRating: results.textAnalysis.averageRating.toFixed(1),
  countries: Object.keys(results.salesAnalysis.salesByCountry).length,
  processingTime: dayjs().format('HH:mm:ss'),
  securityStatus: 'All data encrypted successfully',
  validationPassed: results.validation.filter(v => v.isValid).length + '/' + results.validation.length
};

return results;
